import React  from 'react'
// import axios from "axios"
import { useSearchParams } from 'react-router-dom'
import {  Navigate } from 'react-router-dom';
import axios from 'axios';

async function  getuser(login){
    const res = await axios.get('http://127.0.0.1:8080/EJB-Client/APIv1.0/users/'+login);
    return res.data;
}

export default function Login (){


    // if () {
    //     setTimeout(() => {
    //       return <Redirect to={'/event'} />;
    //     }, 2000);
    //   }

    const [searchParams] = useSearchParams();
    var data={
        "login": searchParams.get("login"),
        "password": searchParams.get("password"),
        "role": "user"
        }


        const user = getuser(data.login)
        console.log(user.password)
        let is_logged=false
        if ( user!=null && user.login===data.login &&  data.password === user.password && user.role=="admin" ){
            localStorage.setItem("userID", "2022@"+data.login+"@"+data.password)
            localStorage.setItem("loggingerror", "no")
             is_logged=true
        }
        else {
            localStorage.setItem("loggingerror", "yes")
             is_logged=false
        }

    return (
        
        <div className='flex my-40 flex-col rid place-items-center h-screen'>
            {is_logged ? <Navigate to="/"   /> : <Navigate to="/signin"  />}
          <p className='align-middle text-xl font-semibold font-serif '>Logged In successfully , redirected to the app .... </p> 
          <img src='https://www.svgrepo.com/show/314685/spinner-solid.svg' alt=' '  className='animate-spin mt-8 w-12 align-middle '/>      
        </div>
    )
}
