import React  from 'react'
import {  Navigate } from 'react-router-dom';
import axios from 'axios';

export default class Login extends React.Component {

    constructor(){
      super()
      this.state={
        user:{},
        queryParams:{
            "login": new URLSearchParams(window.location.search).get('login'),
            "password": new URLSearchParams(window.location.search).get('password'),
        },
        is_logged: true
      }
    }
      componentDidMount = () => {
        this.setState({
            is_logged: false
        });
        this.fetchItems()
        if (event) {
            setTimeout(() => { 
              this.props.history.push('/');
            }, 2000)
          }
      }

    fetchItems = async () => {
    
        const res = await axios.get('http://127.0.0.1:8080/EJB-Client/APIv1.0/users/'+this.state.queryParams.login);
        const item = res.data;
        console.log(item)

        if ( item!=null && item.login===this.state.queryParams.login &&  this.state.queryParams.password === item.password && item.role==="admin" ){
            localStorage.setItem("userID", "2022@"+item.login+"@"+item.password)
            localStorage.setItem("loggingerror", "no")
            this.setState({
                user: item,
                is_logged: true
            });
        }
        else {
            localStorage.setItem("loggingerror", "yes")
            this.setState({
                is_logged: false
            });
        }

        
    }



    
    render(){
        return (
        
            <div className='flex flex-row align-middle justify-center my-40 h-screen'>
                {this.state.is_logged ? <div className=''><p className='text-xl font-semibold font-serif '>Logged In successfully , redirected to the app .... </p> <img src='https://www.svgrepo.com/show/314685/spinner-solid.svg' alt=' '  className='animate-spin mt-8 w-12 '/> </div> : " "}
                   
            </div>
        )
    }
    
}
