import React, { Component } from 'react'
import axios from "axios"
import { useSearchParams } from 'react-router-dom'

export default class Login extends Component {

    constructor(){
        super()
        this.state={
            is_logged: false ,
            data: {
                login: " ",
                password: " ",
                role: " ",
            }
        }
    }

    componentDidMount = () => {
        this.retrieveQueryParams()
        this.verifyLogin()
    }

    retrieveQueryParams= async () =>{
        const [searchParams] = useSearchParams();
        console.log(searchParams.get("login"))

        var params={
        "login": searchParams.get("login"),
        "password": searchParams.get("password"),
        "role": "user"
        }

        this.setState({
            data: params,
        })
    }

    verifyLogin = async () => {
      
        // const res = await axios.get('https://fortnite-api.com/v2/cosmetics/br/');
        // const items = res.data.data.slice(200,300);

        if ( this.state.data.login === "mashateayoub@gmail.com" && this.state.data.password === "ayoub1999"  ){
            localStorage.setItem("userID", this.state.data.login+"@"+this.state.data.password)
            this.setState({
                is_logged: true
            });
        }

        
    }
    




  render() {
      var data=localStorage.getItem("user")
    return (
        
      <div className='flex my-40 flex-col rid place-items-center h-screen'>
          
          <p className='align-middle text-xl font-semibold font-serif '>Logged In successfully , redirected to the app .... {data}</p> 
          <img src='https://www.svgrepo.com/show/314685/spinner-solid.svg' alt=' '  className='animate-spin mt-8 w-12 align-middle '/>        
        </div>
    )
  }
}
