import React, { Component } from 'react';
import axios from "axios";
import Card from '../utils/Card';
import {useSearchParams} from "react-router-dom"






class CardList extends Component {

  constructor(){
    super()
    this.state={
      data:[],
      queryParams:{
        "depart":" ",
        "destination": " ",
        "date": " "
      },
      loading:false
    }
  }
    componentDidMount = () => {
        this.setState({loading: true})
        this.retrievQueryParams()
        this.fetchItems()
    }

    retrievQueryParams= async () =>{
      const [searchParams] = useSearchParams();
      console.log(searchParams.get("date"))
  
      var params={
        "depart":searchParams.get("depart"),
        "destination": searchParams.get("destination"),
        "date": searchParams.get("date")
      }
      this.setState({
        queryParams: params
    });
    }

    fetchItems = async () => {
      
        const res = await axios.get('https://fortnite-api.com/v2/cosmetics/br/');
        const items = res.data.data.slice(200,300);

        // this will re render the view with new data
        this.setState({
            data: items,
            loading: false
        });
    }

    sortString = (string)=>{
      
        return(
          (a, b) => {
            let fa = a.rarity.displayValue.toLowerCase(),
                fb = b.rarity.displayValue.toLowerCase();
        
            if (fa < fb) {
                return -1;
            }
            if (fa > fb) {
                return 1;
            }
            return 0;
        }
        )
    }

    sortByRarity = async () => {
      this.setState({loading: true})
      this.setState(
        ({ data }) => (
          {
            data: data.sort((a, b) => {
              let fa = a.rarity.displayValue.toLowerCase(),
                  fb = b.rarity.displayValue.toLowerCase();
          
              if (fa < fb) {
                  return -1;
              }
              if (fa > fb) {
                  return 1;
              }
              return 0;
          }),
            loading: false
        }));
    }

    sortByName = async () => {
      this.setState({loading: true})
      this.setState(
        ({ data }) => (
          {
            data: data.sort((a, b) => {
              let fa = a.name.toLowerCase(),
                  fb = b.name.toLowerCase();
          
              if (fa < fb) {
                  return -1;
              }
              if (fa > fb) {
                  return 1;
              }
              return 0;
          }),
            loading: false
        }));
    }


  render(){
    
    return(
        <div className="mx-auto px-12 bg-gray-50 min-h-screen " >
            <div className='py-8 flex flex-row justify-between'>
                <div className='font-semibold text-xl italic font-serif'>
                    <h1>Results for your search : {this.state.queryParams.depart} {this.state.queryParams.destination}</h1>
                </div>
              <div className=''>
              <button onClick={this.sortByRarity} className=" font-semibold mr-4 text-white bg-cyan-500 hover:bg-cyan-400 border-0 py-2 px-4 focus:outline-none rounded  " >Sort by Rarity</button>{' '}
              <button onClick={this.sortByName}  className=" font-semibold text-white bg-cyan-500 hover:bg-cyan-400 border-0 py-2 px-4 focus:outline-none rounded  right-0" >Sort by Name</button>
              </div>
            </div>
            
            
            { this.state.loading ? <div className='flex justify-center py-8'><img src='https://www.svgrepo.com/show/314685/spinner-solid.svg' className='animate-spin w-24 ' alt=" " /></div> : <div className='p-10 grid grid-cols-1 sm:grid-cols-1 md:grid-cols-4 lg:grid-cols-4 xl:grid-cols-4 gap-5 align-middle'> {this.state.data.map((item) => (<Card key={item.id} item={item}/>))}</div> }
            
            
        </div>
    );
}
}

export default CardList;
