import React, { Component } from 'react';
import axios from "axios";
import Card from '../utils/Card';






class CardList extends Component {

  constructor(){
    super()
    this.state={
      data:[],
      queryParams:{
        "depart":" ",
        "destination": " ",
        "date": " "
      },
      loading:false
    }
  }
    componentDidMount = () => {
        this.setState({loading: true})
        this.retrieveQueryParams()
        this.fetchOffers()
    }

    retrieveQueryParams= async () =>{
    
      this.setState({
        queryParams: this.props.data
      })
    }



    fetchOffers = async () => {
      const res= await axios({
        method: "GET",
        url: "http://127.0.0.1:8080/EJB-Client/APIv1.0/annonces/",
        headers: { "Content-Type": "Application/json"},
    })
      if(res.status===200 && res.data.con !== 0){
          this.setState({
          data:res.data,
          loading: false
      })}

    }
  

    sortString = (string)=>{
      
        return(
          (a, b) => {
            let fa = a.ville_depart.toLowerCase(),
                fb = b.ville_depart.toLowerCase();
        
            if (fa < fb) {
                return -1;
            }
            if (fa > fb) {
                return 1;
            }
            return 0;
        }
        )
    }

    sortByCity = async () => {
      this.setState({loading: true})
      this.setState(
        ({ data }) => (
          {
            data: data.sort((a, b) => {
              let fa = a.ville_depart.toLowerCase(),
                fb = b.ville_depart.toLowerCase();
          
              if (fa < fb) {
                  return -1;
              }
              if (fa > fb) {
                  return 1;
              }
              return 0;
          }),
            loading: false
        }));
    }

    sortByName = async () => {
      this.setState({loading: true})
      this.setState(
        ({ data }) => (
          {
            data: data.sort((a, b) => {
              let fa = a.name.toLowerCase(),
                  fb = b.name.toLowerCase();
          
              if (fa < fb) {
                  return -1;
              }
              if (fa > fb) {
                  return 1;
              }
              return 0;
          }),
            loading: false
        }));
    }


  render(){
    
    return(
        <div className="mx-auto px-12 bg-gray-50 min-h-screen " >
            <div className='py-8 flex flex-row justify-between'>
                <div className='font-semibold text-xl italic font-serif'>
                  <h1>Results for your search : {window.location.href.indexOf("depart") > -1 ? <span className='font-light text-lg'> {this.state.queryParams.depart},  {this.state.queryParams.destination}, {this.state.queryParams.date}</span> : " " }</h1>
                </div>
              <div className=''>
              <button onClick={this.sortByCity} className=" font-semibold mr-4 text-white bg-cyan-500 hover:bg-cyan-400 border-0 py-2 px-4 focus:outline-none rounded  " >Sort by City</button>{' '}
              <button onClick={this.sortByName}  className=" font-semibold text-white bg-cyan-500 hover:bg-cyan-400 border-0 py-2 px-4 focus:outline-none rounded  right-0" >Sort by Name</button>
              </div>
            </div>
            
            { this.state.loading ? <div className='flex justify-center py-8'><img src='https://www.svgrepo.com/show/314685/spinner-solid.svg' className='animate-spin w-24 ' alt=" " /></div> : <div className='p-10 grid grid-cols-1 sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-3 gap-5 align-middle'> {this.state.data.map((item) => (<Card key={item.id} item={item}/>))}</div> }
            
            
        </div>
    );
}
}

export default CardList;
