import React  from 'react'
import {  Link } from 'react-router-dom';
import axios from 'axios';

export default class AccountPage extends React.Component {

    constructor(){
      super()
      this.state={
        user:{},
        queryParams:{
            "login": new URLSearchParams(window.location.search).get('login'),
            "password": new URLSearchParams(window.location.search).get('password'),
            "firstname":new URLSearchParams(window.location.search).get('first name'),
            "lastname":new URLSearchParams(window.location.search).get('last name'),
            "age":new URLSearchParams(window.location.search).get('Age'),
            "gender":new URLSearchParams(window.location.search).get('sexe'),
            "phone":new URLSearchParams(window.location.search).get('phone'),
            "email":new URLSearchParams(window.location.search).get('Email'),
            "adress":new URLSearchParams(window.location.search).get('adress'),
        },
        is_created: false
      }
    }


      componentDidMount = () => {

        this.postUser()
        
      }

    postUser = async () => {

        var bodyFormData1 = new FormData();
        bodyFormData1.append('login', this.state.queryParams.login);
        bodyFormData1.append('password', this.state.queryParams.password); 
        bodyFormData1.append('role', "user"); 

        axios({
            method: "POST",
            url: "http://127.0.0.1:8080/EJB-Client/APIv1.0/users/",
            data: bodyFormData1,
            headers: { "Content-Type": "Application/json"},
          })
            .then(function (response) {
              //handle success
              console.log(response);
            })
            .catch(function (response) {
              //handle error
              console.log(response);
            });

    //     if ( item!=null && item.login===this.state.queryParams.login &&  this.state.queryParams.password === item.password && item.role==="user" ){
