import React  from 'react'
import { useSearchParams } from 'react-router-dom'
import {  Navigate } from 'react-router-dom';
import axios from 'axios';

export default class Login extends React.Component {

    constructor(){
      super()
      this.state={
        user:{},
        queryParams:{
          "login":" ",
          "password": " ",
        },
        is_logged:false
      }
    }
      componentDidMount = () => {
          this.retrieveQueryParams()
          this.fetchItems()
      }
  
      retrieveQueryParams= async () =>{
        const query = new URLSearchParams(window.location.search);
        
        const login = query.get('login');
        const password = query.get('password');
        console.log(login, password)
        var data={
            "login": login,
            "password": password,
            }

        this.setState({
          queryParams: data
        })
      }

    fetchItems = async () => {
        
    
        const res = await axios.get('http://127.0.0.1:8080/EJB-Client/APIv1.0/users/'+this.state.queryParams.login);
        const item = res.data;

        let l=false
        if ( this.state.user!=null && this.state.user.login===this.state.data.login &&  this.state.data.password === this.state.user.password && this.state.user.role=="admin" ){
            localStorage.setItem("userID", "2022@"+this.state.user.login+"@"+this.state.user.password)
            localStorage.setItem("loggingerror", "no")
             l=true
        }
        else {
            localStorage.setItem("loggingerror", "yes")
             l=false
        }

        this.setState({
            user: item,
            is_logged: l
        });
    }



    
    render(){

        return (
        
            <div className='flex my-40 flex-col rid place-items-center h-screen'>
                {this.state.is_logged ? <Navigate to="/"   /> : <Navigate to="/signin"  />}
              <p className='align-middle text-xl font-semibold font-serif '>Logged In successfully , redirected to the app .... </p> 
              <img src='https://www.svgrepo.com/show/314685/spinner-solid.svg' alt=' '  className='animate-spin mt-8 w-12 align-middle '/>      
            </div>
        )
    }
    
}
