import React from 'react';
import ResponsiveNavBar from "../Partials/Header"
import SearchForm from '../utils/SearchForm';
import CardList from '../Partials/CardList';
import {useSearchParams} from "react-router-dom"


export default class Ride extends Component {

  constructor(){
      super()
      this.state={
        data:[],
        con: localStorage.getItem("id_con"),
        is_loaded: false
      }
    }


      componentDidMount = () => {

        this.fetchOffers()
        
      }

    fetchOffers = async () => {
      const res= await axios({
        method: "GET",
        url: "http://127.0.0.1:8080/EJB-Client/APIv1.0/annonces/",
        headers: { "Content-Type": "Application/json"},
    })
      if(res.status===200 && res.data.con !== 0){
          this.setState({
          data:res.data,
          is_loaded: true
      })}
      else{
        this.setState({
          is_loaded: false
        })
        }
    }
    

render() {
  
  var queryParams={
    "depart": new URLSearchParams(window.location.search).get('depart') ,
    "destination": new URLSearchParams(window.location.search).get('destination') ,
    "date":new URLSearchParams(window.location.search).get('date') 
  }
  return (
    <div className="flex flex-col min-h-screen overflow-hidden">

      {/*  Site header */}
      <ResponsiveNavBar />

      {/*  Page content */}
      <main >

        {/* form */}
        
          <div className='flex py-4 justify-center align-center  bg-cyan-500 text-white drop-shadow-xl'>
          <div style={{width: "1200px"}}>
          <SearchForm  />
          </div>
        
        </div>


        {/* list */}
        <div>
            <CardList data={queryParams} />
        </div>
       
        
     </main>

      {/*  Site footer */}

    </div>
  );
}
}
