import React from 'react'
import Header from '../../Partials/Driver/Header'
import axios from 'axios'

export default class UpdateOffer extends React.Component {
  constructor(props){
    super(props)
    this.state={
        id:new URLSearchParams(window.location.search).get('id'),
        offer: {},
        is_updated: false
    }
  }
componentDidMount = async ()=>{
    this.fetchoffer()
}

fetchOffer = async () => {
  const res= await axios({
    method: "GET",
    url: "http://127.0.0.1:8080/EJB-Client/APIv1.0/annonces/"+this.state.id,
    headers: { "Content-Type": "Application/json"},
})
if(res.status===200){
    this.setState({
        offer: res.data
    })

}
}
handleUpdate = async () => {
    const res= await axios({
        method: "PUT",
        url: "http://127.0.0.1:8080/EJB-Client/APIv1.0/annonces?mili=",
        data: {},
        headers: { "Content-Type": "Application/json"},
    })
    if(res.status===200){
        this.setState({
            is_deleted: true
        })
    }




}


  render(){
    return (
      <div className="min-h-full">
          <Header />
  
  
    <header className="bg-white shadow">
      <div className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        <h1 className="text-3xl font-bold text-gray-900">Update Offer</h1>
      </div>
    </header>
  
    </div>
    )

  }
  
}
