import React, { Component } from 'react'
import ResponsiveNavBar from "../Partials/Header"
import axios from 'axios'

export default class OfferPage extends Component {

  constructor(){
    super()
    this.state={
      id:new URLSearchParams(window.location.search).get('id'),
      offer: {},
      // depart: res.data.ville_depart,
      // dest: res.data.ville_arrivee,
      // date: res.data.date_depart.split('Z')[0].split("-"),
      // mindate: res.data.date_depart.split('Z')[0].split("-"),
      // nombre_places: res.data.nombre_places,
      // prix: res.data.prix,
      // time:time,
      // HMS: res.data.time.split("T")[1].split('Z')[0].split(":")
    }
  }

  componentDidMount=()=>{
    this.fetchOffer()
  }

  fetchOffer =  () => {
    axios({
      method: "GET",
      url: "http://127.0.0.1:8080/EJB-Client/APIv1.0/annonces/"+this.state.id,
      headers: { "Content-Type": "Application/json"},
        }).then((res) => {
              if(res.status===200){
                let[h,mm,s] =res.data.time.split("T")[1].split('Z')[0].split(":")
                let [y,m,d] =res.data.time.split("T")[0].split("-")
                let time=new Date(y,m,d,h,mm,s).getTime()
                console.log(time)
                this.setState({
                    offer: res.data,
                    // depart: res.data.ville_depart,
                    // dest: res.data.ville_arrivee,
                    // date: res.data.date_depart.split('Z')[0].split("-"),
                    // mindate: res.data.date_depart.split('Z')[0].split("-"),
                    // nombre_places: res.data.nombre_places,
                    // prix: res.data.prix,
                    // time:time,
                    // HMS: res.data.time.split("T")[1].split('Z')[0].split(":")
                  })
            }});
      

  }

  render() {
    return (
      <div className="flex flex-col min-h-screen overflow-hidden">

      {/*  Site header */}
      <ResponsiveNavBar />



      {/*  Site footer */}
      <div className='bg-gray-100 min-h-full py-80'>{this.state.id}</div>
      <div className='bg-gray-100 min-h-full py-80'>{this.state.offer.ville_depart}</div>

    </div>
    )
  }
}
