import React, { Component } from 'react'
import axios from "axios";

export default class ResCard extends Component {

  constructor(props) {
    super(props)
    this.state = {
      path: window.location.pathname,
      item: props.item,
      offer: {},
      time: '',
      date_depart: '',
    }
  }

  componentDidMount() {
    this.fetchOffer()
  }

  fetchOffer = async () => {
    const res = await axios({
      method: "GET",
      url: "http://127.0.0.1:8080/EJB-Client/APIv1.0/annonces/" + this.state.item.id_annonces,
      headers: { "Content-Type": "Application/json" },
    })
    if (res.status === 200) {
      let t = new Date(res.data.time.split("T")[0] + " " + res.data.time.split("T")[1].split("Z")[0])
      let d = res.data.date_depart.split("Z")[0].split("-")
      this.setState({
        offer: res.data,
        date_depart: d[2] + "/" + d[1] + "/" + (parseInt(d[0]) + 1),
        time: t.toLocaleTimeString()
      })
    }


  }



  render() {
    const {
      prix,
      ville_arrivee,
      ville_depart,
    } = this.state.offer

    if (this.state.path.startsWith("/driver/reservations")) {
      return (
        <div class=" mx-auto w-full" >

          <div class=" rounded cursor-auto shadow md:flex bg-gray-200"  >
            <div >
              <div class="flex justify-between items-center mx-auto" >
                <div class=" text-xs uppercase font-bold tracking-wider bg-gray-300 inline-flex px-2 py-1 rounded mr-2" >
                  <svg xmlns="http://www.w3.org/2000/svg" class="h-4 w-4 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor" stroke-width="2">
                    <path stroke-linecap="round" stroke-linejoin="round" d="M17 9V7a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2m2 4h10a2 2 0 002-2v-6a2 2 0 00-2-2H9a2 2 0 00-2 2v6a2 2 0 002 2zm7-5a2 2 0 11-4 0 2 2 0 014 0z" />
                  </svg>
                  <span> {prix} DH</span>
                </div>
                <div class="text-xs uppercase font-bold tracking-wider bg-gray-300 inline-flex px-2 py-1 rounded mr-2" >
                  <svg xmlns="http://www.w3.org/2000/svg" class="h-4 w-4 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor" stroke-width="2">
                    <path stroke-linecap="round" stroke-linejoin="round" d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
                  </svg>
                  <span> {this.state.date_depart}</span>

                </div>

                <div class="text-xs uppercase font-bold tracking-wider bg-gray-300 inline-flex px-2 py-1 rounded mr-2" >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-4 mr-1 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}>
                    <path strokeLinecap="round" strokeLinejoin="round" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                  <span> {this.state.time}</span>
                </div>
              </div>


              <div class="flex  mx-auto mt-8">
                <p class="text-xl font-semibold" >
                  <span><svg xmlns="http://www.w3.org/2000/svg" class="h-6 w-6  " fill="none" viewBox="0 0 24 24" stroke="currentColor" stroke-width="2">
                    <path stroke-linecap="round" stroke-linejoin="round" d="M13 5l7 7-7 7M5 5l7 7-7 7" />
                  </svg>{ville_depart}</span>
                </p>
                <p class=" text-xl" >{ville_arrivee}</p>
                <button class="bg-cyan-500 hover:bg-cyan-700 text-white font-bold py-2 px-4 rounded-full">Confirm</button>
              </div>

            </div>
          </div>

        </div>

      )
    }

  }
}
