import React, { Component } from 'react';
import axios from "axios";
import Card from '../utils/Card';





class CardList extends Component {

  constructor(){
    super()
    this.state={
      data:[],
      loading:false
    }
  }
    componentDidMount = () => {
        this.setState({loading: true})
        this.fetchItems()
    }

    fetchItems = async () => {
        const res = await axios.get('https://fortnite-api.com/v2/cosmetics/br/');
        const items = res.data.data.slice(200,300);

        // this will re render the view with new data
        this.setState({
            data: items,
            loading: false
        });
    }

    sortString = (string)=>{
      
        return(
          (a, b) => {
            let fa = a.rarity.displayValue.toLowerCase(),
                fb = b.rarity.displayValue.toLowerCase();
        
            if (fa < fb) {
                return -1;
            }
            if (fa > fb) {
                return 1;
            }
            return 0;
        }
        )
    }

    sortByRarity = async () => {
      this.setState({loading: true})
      this.setState(
        ({ data }) => (
          {
            data: data.sort((a, b) => {
              let fa = a.rarity.displayValue.toLowerCase(),
                  fb = b.rarity.displayValue.toLowerCase();
          
              if (fa < fb) {
                  return -1;
              }
              if (fa > fb) {
                  return 1;
              }
              return 0;
          }),
            loading: false
        }));
    }

    sortByName = async () => {
      this.setState({loading: true})
      this.setState(
        ({ data }) => (
          {
            data: data.sort((a, b) => {
              let fa = a.name.toLowerCase(),
                  fb = b.name.toLowerCase();
          
              if (fa < fb) {
                  return -1;
              }
              if (fa > fb) {
                  return 1;
              }
              return 0;
          }),
            loading: false
        }));
    }


  render(){
    
    return(
        <div className="mx-auto px-12 " >
            <div className='py-8 flex flex-col'>
                <div className=' left-0 absolute font-extrabold text-lg'>
                    <h1>heeeeeeeeeeeeey</h1>
                </div>
              <div className='relative right-0'>
              <button onClick={this.sortByRarity} className="text-base bg-cyan-500 font-semibold text-white rounded-md py-2 px-4 " >Sort by Rarity</button>{' '}
              <button onClick={this.sortByName}  className="text-base bg-cyan-500 font-semibold text-white rounded-md py-2 px-4" >Sort by Name</button>
              </div>
            </div>
            
            {/* <Row className='flex justify-content-center p-4'> 
            { this.state.loading ? <Spinner type="border" role="status" ></Spinner> : this.state.data.map((item) => (<Card key={item.id} item={item}/>))}
            </Row> */}
            
        </div>
    );
}
}

export default CardList;
